import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Button, ImageBackground, StyleSheet, Text, View } from 'react-native';
import background from './assets/images/background.png';

export default function App() {
  return (
    <View style={styles.container}>
      <ImageBackground source={background} resizeMode="cover" style={styles.image}>
      <Text>le Bateaux de Thibault</Text>
      <Text>Vent en direct de notre bateau</Text>
      <Text>Produits selon la saison, Livraisons sur Paris</Text>
      <Text>06.63.99.99.78</Text>
      <Text>lebateaudethibault@gmail.com</Text>
      <Text>www.facebook.com/lebateaudethibault</Text>
      <View style={{ marginTop: '2rem', width: "90%" }}>
        <View style={{ width: '100%' }}>
          <Button title="Produits et promotions" />
        </View>

        <View style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          width: '100%'
        }}>
          <Button title="Bateaux"
          />
          <Button title="Restaurants"
          />
          <Button title="Recettes"
          />
          <Button title="Contact"
          />
        </View>
      </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: "center"
  },
  text: {
    color: "white",
    fontSize: 42,
    lineHeight: 84,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#000000c0"
  }
});
